sys_variable_value.value:
(function execute(inputs, outputs) {
    outputs.id = restCall();

    function restCall() {
        var id = '';
        try {
            var issue = {
                "title": inputs.title,
                "body": inputs.body,
              	"labels": [inputs.labels]
            };
            var rest = new sn_ws.RESTMessageV2('x_0586_til.Github', 'Create Issue');
            rest.setStringParameterNoEscape('owner', 'iamkalai');
            rest.setStringParameterNoEscape('repo', 'SNTIL');
            rest.setRequestBody(JSON.stringify(issue));
            var response = rest.execute();
            var httpStatus = response.getStatusCode();
            if (httpStatus == "201") {
                var result = JSON.parse(response.getBody());
                id = result.id;
                outputs.number = result.number;

            }
        } catch (ex) {
            //var message = ex.message;
            id = "";
        }

        return id;
    }
})(inputs, outputs);

