sys_variable_value.value:
(function execute(inputs, outputs) {
    outputs.id = restCall();

    function restCall() {
        var id = '';
        try {
            var card = {
                "content_id": inputs.issue,
                "content_type":"Issue"
            };
            var rest = new sn_ws.RESTMessageV2('x_0586_til.Github', 'Create Project Card');
            rest.setStringParameterNoEscape('column_id', '9419507');
            rest.setRequestBody(JSON.stringify(card));
            var response = rest.execute();
            var httpStatus = response.getStatusCode();
            if (httpStatus == "201") {
                var result = JSON.parse(response.getBody());
                id = result.id;
            }
        } catch (ex) {
            //var message = ex.message;
            id = "";
        }

        return id;
    }
})(inputs, outputs);

